import { useState } from "react";
import "./Weather_container.css";

function Weather_container() {
  const left_style = {
    position: "relative",
    left: "-0px",
  };

  const forecastbutton_style = {
    position: "relative",
    left: "50px",
  };

  const forecastbox_style = {
    position: "relative",
    right: "20px",
    top: "2px",
  };

  const right_style = {
    position: "relative",
    left: "300px",
    bottom: "-1px",
  };

  const middle_style = {
    position: "relative",
    left: "-300px",
  };

  const AI_imgstyle = {
    width: "350px",
    border: "5px solid #1c1104a5",
    padding: "3px",
    margin: "-20px",
    borderRadius: "8px", //use that instead of border-radius
  };
  //collapse button 

  const [isCollapsed, setIsCollapsed] = useState(true);

  const handleButtonClick = () => {
    setIsCollapsed(!isCollapsed);
  };



  //input grabbers for API

  const [city1, setcity1] = useState("");
  const [temperature, setTemperature] = useState(null);
  const [city, setCity] = useState("");
  const [unit, setUnit] = useState("F°");
  const [uvIndex, setUvIndex] = useState(null);
  const [timeZone, setTimeZone] = useState("");
  const [localTime, setLocalTime] = useState("");
  const [feelsLike, setFeelsLike] = useState(null);
  const [description, setDescription] = useState("");
  const [aqiIndex, setAqiIndex] = useState(null);
  const [isDay, setIsDay] = useState("");

  const [city2, setcity2] = useState("");

  //click description

  const handleClick = () => {
    if (city1.trim() === "") {
      alert("Please enter a city name.");
      return;
    }
    var query = city1;
    var apiKey = "1182cdc066324cd080403024231607"; // Replace with your actual WeatherAPI key
    var url =
      "https://api.weatherapi.com/v1/current.json?key=" +
      apiKey +
      "&q=" +
      query +
      "&aqi=yes&lang=en";

    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setTemperature(data.current.temp_f);
        setCity(data.location.name);
        setUnit("F°"); // Or you can set the unit based on data received from the API
        setUvIndex(data.current.uv);
        setTimeZone(data.location.tz_id);
        setLocalTime(data.location.localtime);
        setFeelsLike(data.current.feelslike_f);
        setDescription(data.current.condition.text);
        setAqiIndex(data.current.air_quality.pm2_5);
        setIsDay(data.current.is_day);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  //click description forecast left field

  const [forecastData, setForecastData] = useState([]);


  const handleClickforecast = () => {
    if (city.trim() === "") {
      alert("Please enter a city name.");
      return;
    }
    var query = city;
    var apiKey = "1182cdc066324cd080403024231607"; // Replace with your actual WeatherAPI key
    var url =
      "https://api.weatherapi.com/v1/forecast.json?key=" +
      apiKey +
      "&q=" +
      query +
      "&days=3";
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        var forecast = data.forecast.forecastday;
        setForecastData(forecast);
        })
      .catch((error) => {
        console.error("Error:", error);
      });
  };
  return (
    <div className="weather_container">
      <div className="left_field" style={left_style}>
        <input
          type="text"
          required
          value={city1}
          onChange={(e) => setcity1(e.target.value)}
          id="textInput"
          placeholder="1. Current City"
        ></input>

        <button onClick={handleClick} id="button-73" role="button">
          Search
        </button>
        <p>City: {city} </p>
        <p>Time: {localTime}</p>
        <p>Timezone: {timeZone}</p>
        <i id="weatherIcon" className=""></i>
        <p>
          {temperature} {unit}
        </p>
        <p>
          Feels like: {feelsLike} {unit}
        </p>
        <p>Description: {description}</p>
        <p>UV Index: {uvIndex}</p>
        <p>AQI Index: {aqiIndex}</p>
        <br></br>
        <button
          id="forecastButton"
          onClick={handleClickforecast}
          className="arrow-button"
          style={forecastbutton_style}
        >
          Forecast
        </button>
        <div
          id="forecastBox"
          className="forecast-box"
          style={forecastbox_style}
        ></div>
        <p class="d-inline-flex gap-1">
          <button
            className="btn btn-primary"
            type="button"
            onClick={handleButtonClick}
            aria-expanded= {!isCollapsed}
            aria-controls="collapseExample"
          >
            Button with data-bs-target
          </button>
        </p>
        <div className={`collapse${isCollapsed ? '' : ' show'}`} id="collapseExample">
          <div className="card card-body">
            Some placeholder content for the collapse component. This panel is
            hidden by default but revealed when the user activates the relevant
            trigger.
          </div>
        </div>
      </div>
      <div className="right_field" style={right_style}>
        <input
          type="text"
          id="textInput1"
          placeholder="2. Destination city"
        ></input>
        <button id="button-73" role="button">
          Search
        </button>
        <p>
          City: <span id="city1"></span>
        </p>
        <p>
          Time: <span id="local_time1"></span>
        </p>
        <p>
          Timezone: <span id="timezone1"></span>
        </p>
        <i id="weatherIcon1" className=""></i>
        <p>
          {" "}
          <span id="degrees1"></span> <span id="unit"></span>
        </p>
        <p>
          Feels like: <span id="feels_like1"></span> <span id="unitf"></span>
        </p>
        <p>
          Description: <span id="description1"></span>
        </p>
        <p>
          UV Index: <span id="uv1"></span>
        </p>
        <p>
          AQI Index: <span id="aqi1"></span>
        </p>
        <br></br>
        <button
          id="forecastButton1"
          className="arrow-button"
          style={forecastbutton_style}
        >
          Forecast
        </button>
        <div
          id="forecastBox1"
          className="forecast-box"
          style={forecastbox_style}
        ></div>
      </div>

      <div className="middle_field" style={middle_style}>
        <img src="images/logo1.png" alt="Profile" style={AI_imgstyle}></img>
        <input
          type="text"
          id="in_advance"
          placeholder="3. How many days from now is the trip?"
        ></input>
        <p>Max: 3 days in advance</p>
        <button id="button-75" role="button">
          Load Suggestions
        </button>
        <p>
          <span id="Suggestions"></span>
        </p>
      </div>
    </div>
  );
}

export default Weather_container;
