    var apiKey = '1182cdc066324cd080403024231607'; // Replace with your actual WeatherAPI key
    var url = 'https://api.weatherapi.com/v1/forecast.json?key=' + apiKey + '&q=' + query + '&days=3&aqi=yes&lang=en';
